mapper.select:
SELECT CONCAT( ( @rowNo := @rowNo + 1 ) ) AS rowNo, T007.customerNo, T007.customerName, M008.levelName, T007.stationCode, M013.stationName, CASE WHEN T007.listedCompanyFlag = "0" THEN CONCAT( M007.companyNatureName, "(上場)" ) ELSE M007.companyNatureName END AS companyNatureName, T008.topCustomerName, T006.employeeNo, CONCAT( T001.employeeFristName, T001.employeeLastName ) AS employeeName, T006.stationCode AS siteLocationCode, M013SITE.stationName AS location, T006.siteManager, T006.unitPrice, T006.admissionStartDate, T006.admissionEndDate, T007.paymentsiteCode, T007.capitalStock, T007.purchasingManagers, M020.paymentSiteName, T007.establishmentDate, T007.businessStartDate FROM T007CustomerInfo AS T007 LEFT JOIN T006EmployeeSiteInfo AS T006 ON T007.customerNo = T006.customerNo LEFT JOIN T001Employee AS T001 ON T006.employeeNo = T001.employeeNo LEFT JOIN M013Station AS M013 ON T007.stationCode = M013.stationCode AND T007.stationCode != "" LEFT JOIN M013Station AS M013SITE ON T006.stationCode = M013SITE.stationCode AND T006.stationCode != "" LEFT JOIN M008Level AS M008 ON T007.levelCode = M008.levelCode AND T007.levelCode != "" LEFT JOIN M007CompanyNature AS M007 ON T007.companyNatureCode = M007.companyNatureCode AND T007.companyNatureCode != "" LEFT JOIN M020PaymentSite AS M020 ON T007.paymentsiteCode = M020.paymentSiteCode AND T007.paymentsiteCode != "" LEFT JOIN T008TopCustomerInfo AS T008 ON T007.topCustomerNo = T008.topCustomerNo, ( SELECT @rowNo := 0 ) AS rowNo WHERE 1 = 1
AND T007.customerNo=#{customerNo}
AND T007.customerName like concat('%',#{customerName},'%')
AND T007.levelCode= #{levelCode}
AND T007.companyNatureCode= #{companyNatureCode}
AND T007.topCustomerNo = #{topCustomerNo}
AND T007.businessStartDate >= #{businessStartDate}
AND T007.paymentsiteCode = #{paymentsiteCode}
AND T007.stationCode = #{stationCode}
AND CAST(T007.capitalStock AS decimal) >= #{capitalStockFront}
AND CAST(T007.capitalStock AS decimal) <= #{capitalStockBack}
AND T007.basicContract = #{basicContract}
ORDER BY T007.customerNo
