mapper.select:
SELECT T005.reflectYearAndMonth, M005.employeeFormName, T007.customerName, T006.unitPrice, T005.salary, T005.transportationExpenses, T005.SocialInsuranceFlag,
T005.insuranceFeeAmount,
T005.bonusFlag,
T005.scheduleOfBonusAmount,
T005.leaderAllowanceAmount, T005.otherAllowanceAmount FROM T001Employee As T001 Left JOIN T005ExpensesInfo As T005 ON T001.employeeNo = T005.employeeNo LEFT JOIN M005EmployeeForm As M005 ON T005.employeeFormCode = M005.employeeFormCode LEFT JOIN T006EmployeeSiteInfo As T006 ON T005.employeeNo = T006.employeeNo LEFT JOIN T007CustomerInfo As T007 ON T006.customerNo =T007.customerNo WHERE T001.employeeFristName = #{employeeFirstName} AND T001.employeeLastName = #{employeeLastName}
AND T005.employeeNo=#{employeeNo}
AND left(reflectYearAndMonth,4) =#{fiscalYear}
