mapper.select:
SELECT T002EmployeeDetail.intoCompanyCode employeeStatus, concat(T001Employee.employeeFristName,T001Employee.employeeLastName) employeeName, T007CustomerInfo.customerAbbreviation customerName, T006EmployeeSiteInfo.admissionStartDate, T006EmployeeSiteInfo.admissionEndDate, T006EmployeeSiteInfo.unitPrice FROM T010SalesSituation INNER JOIN T001Employee ON T010SalesSituation.employeeNo = T001Employee.employeeNo LEFT JOIN T002EmployeeDetail ON T002EmployeeDetail.employeeNo = T010SalesSituation.employeeNo INNER JOIN T006EmployeeSiteInfo ON T010SalesSituation.employeeNo = T006EmployeeSiteInfo.employeeNo AND T010SalesSituation.salesYearAndMonth = SUBSTRING(T006EmployeeSiteInfo.admissionStartDate, 1, 6) AND T010SalesSituation.salesProgressCode = '4' INNER JOIN T007CustomerInfo ON T006EmployeeSiteInfo.customerNo = T007CustomerInfo.customerNo WHERE
T010SalesSituation.salesStaff = #{eigyou}
AND T002EmployeeDetail.intoCompanyCode = #{intoCompanyCode}
AND NOT (T006EmployeeSiteInfo.admissionStartDate > #{endDate} OR T006EmployeeSiteInfo.admissionEndDate < #{startDate})

SELECT customerNo,if(customerAbbreviation is null or
customerAbbreviation = "",customerName,customerAbbreviation) as
customerName FROM T007CustomerInfo;
