mapper.select:
SELECT a.employeeNo, a.systemName, a.unitPrice, a.admissionStartDate, a.admissionEndDate, a.developLanguageCode, a.developLanguageCode2, b.siteRoleName, IF ( c.customerAbbreviation IS NULL OR trim( c.customerAbbreviation ) = '', c.customerName, c.customerAbbreviation ) AS customerName, CONCAT( g.employeeFristName, g.employeeLastName ) AS employeeName, e.developLanguageName, f.stationName AS station, h.bpBelongCustomerCode, ( SELECT IF ( customerAbbreviation IS NULL OR trim( customerAbbreviation ) = '', customerName, customerAbbreviation ) FROM T007CustomerInfo AS T007 LEFT JOIN T011BpInfoSupplement AS T011 ON T007.customerNo = T011.bpBelongCustomerCode WHERE T011.bpEmployeeNo = a.employeeNo and T011.unitPriceStartMonth = "" ) AS employeeFrom FROM T006EmployeeSiteInfo a LEFT JOIN M012SiteRole b ON a.siteRoleCode = b.siteRoleCode AND a.siteRoleCode != '' LEFT JOIN T007CustomerInfo c ON a.customerNo = c.customerNo AND a.customerNo != '' LEFT JOIN T002EmployeeDetail d ON a.employeeNo = d.employeeNo AND a.employeeNo != '' LEFT JOIN M011DevelopLanguage e ON a.developLanguageCode = e.developLanguageCode AND a.developLanguageCode != '' LEFT JOIN M013Station f ON a.stationCode = f.stationCode AND a.stationCode != '' LEFT JOIN T001Employee g ON a.employeeNo = g.employeeNo AND a.employeeNo != '' LEFT JOIN T011BpInfoSupplement h ON a.employeeNo = h.bpEmployeeNo and h.unitPriceStartMonth = "" AND a.employeeNo != '' WHERE 1 =1
AND a.employeeNo = #{employeeNo}
AND a.stationCode = #{stationCode}
AND a.siteRoleCode = #{siteRoleCode}
AND a.customerNo = #{customerNo}
AND a.topCustomerNo = #{topCustomerNo}
AND h.bpBelongCustomerCode = #{bpCustomerNo}
AND a.typeOfIndustryCode = #{typeOfIndustryCode}
AND a.payOffRange1 = #{payOffRange1}
AND a.payOffRange1 = #{payOffRange1}
AND a.payOffRange2 = #{payOffRange2}
AND a.payOffRange2 = #{payOffRange2}
AND cast(a.unitPrice as signed) >= cast(#{unitPrice1} as signed)
AND cast(a.unitPrice as signed) <= cast(#{unitPrice2} as signed)
AND a.developLanguageCode = #{developLanguageCode} or a.developLanguageCode2 = #{developLanguageCode}
AND a.admissionStartDate >= #{admissionStartDate}
AND a.admissionEndDate <= #{admissionEndDate}
AND a.admissionEndDate IS NULL
AND d.employeeStatus = #{employeeStatus}
AND d.employeeFormCode !='3'
AND d.employeeFormCode = '3'
AND (a.scheduledEndDate <= #{scheduledEndDate} OR LEFT(a.admissionEndDate,6) <= #{scheduledEndDate})
AND a.typteOfContractCode = #{typteOfContractCode}
ORDER BY a.employeeNo, a.admissionStartDate ASC
