mapper.select:
SELECT T005.reflectYearAndMonth, T005.employeeNo, CONCAT(T001.employeeFristName,T001.employeeLastName)as employeeName, M005.employeeFormName, T002.employeeFormCode, T005.salary, T005.socialInsuranceFlag, T005.fristBonusMonth as nextBonusMonth, CAST(T005.insuranceFeeAmount As unsigned) As insuranceFeeAmount, CAST(T005.fristTimeBonusAmount + T005.secondTimeBonusAmount As unsigned) As scheduleOfBonusAmount, CAST(T005.fristTimeBonusAmount As unsigned) As lastTimeBonusAmount, T005.remark, T002.intoCompanyYearAndMonth, T002.retirementYearAndMonth FROM T001Employee As T001 LEFT JOIN T005WagesInfo As T005 on T001.employeeNo = T005.employeeNo LEFT JOIN M005EmployeeForm As M005 on T005.employeeFormCode = M005.employeeFormCode LEFT JOIN T002EmployeeDetail As T002 on T001.employeeNo = T002.employeeNo WHERE reflectYearAndMonth between #{startYandM} and #{endYandM}
AND intoCompanyYearAndMonth between #{startYandM} and #{endYandM}
AND retirementYearAndMonth between #{startYandM} and #{endYandM}
order by employeeNo,reflectYearAndMonth

union
SELECT * FROM( SELECT T005f.reflectYearAndMonth, T005f.employeeNo, CONCAT(T001f.employeeFristName,T001f.employeeLastName)as employeeName, M005f.employeeFormName, T005f.salary, T005f.fristBonusMonth as nextBonusMonth, T005f.socialInsuranceFlag, CAST(T005f.insuranceFeeAmount As unsigned) As insuranceFeeAmount, CAST(T005f.fristTimeBonusAmount + T005f.secondTimeBonusAmount As unsigned) As scheduleOfBonusAmount, T005f.remark FROM T005WagesInfo As T005f LEFT JOIN T001Employee As T001f on T001f.employeeNo = T005f.employeeNo LEFT JOIN M005EmployeeForm As M005f on T005f.employeeFormCode = M005f.employeeFormCode WHERE T005f.employeeNo =#{tempList.getEmpNo} and reflectYearAndMonth < #{tempList.getYandM} ORDER BY ABS(#{tempList.getYandM} - reflectYearAndMonth) ASC limit 1 )As A${tempList.getYandM}
order by employeeNo,reflectYearAndMonth
